css:
/* Universal Workshop Bilingual Invoice CSS */
.invoice-container {
    font-family: 'Segoe UI', Tahoma, Arial, sans-serif;
    max-width: 210mm;
    margin: 0 auto;
    padding: 20px;
    color: #333;
    line-height: 1.4;
}

/* Arabic Font Support */
.invoice-container[dir="rtl"] {
    font-family: 'Tahoma', 'Arial Unicode MS', 'Segoe UI', sans-serif;
    text-align: right;
}

/* Header Styles */
.invoice-header {
    margin-bottom: 20px;
}

.company-section {
    border-bottom: 2px solid #000;
    padding-bottom: 15px;
    margin-bottom: 15px;
}

.company-name {
    font-size: 24px;
    font-weight: bold;
    margin: 0 0 10px 0;
    color: #1a73e8;
}

.company-address,
.company-contact {
    font-size: 12px;
    margin: 5px 0;
}

.tax-id {
    font-size: 14px;
    margin-top: 10px;
    padding: 5px;
    background-color: #f0f0f0;
    border-left: 3px solid #1a73e8;
}

.invoice-container[dir="rtl"] .tax-id {
    border-left: none;
    border-right: 3px solid #1a73e8;
}

/* Invoice Title */
.invoice-title h1 {
    font-size: 28px;
    font-weight: bold;
    margin: 0;
    color: #d32f2f;
}

/* Customer and Invoice Details */
.customer-details h3,
.invoice-details h3 {
    font-size: 16px;
    font-weight: bold;
    margin: 0 0 10px 0;
    color: #1a73e8;
    border-bottom: 1px solid #ddd;
    padding-bottom: 5px;
}

.customer-name {
    font-size: 16px;
    margin: 10px 0;
}

.customer-address,
.customer-tax-id {
    font-size: 12px;
    margin: 5px 0;
}

/* Items Table */
.items-table {
    font-size: 12px;
}

.items-table th {
    background-color: #1a73e8;
    color: white;
    font-weight: bold;
}

.items-table td,
.items-table th {
    border: 1px solid #000;
    padding: 8px;
}

.items-table tbody tr:nth-child(even) {
    background-color: #f9f9f9;
}

/* Totals Section */
.totals-section table {
    font-size: 14px;
}

.totals-section td:last-child {
    font-weight: bold;
}

/* QR Code Section */
.qr-code-section h4 {
    font-size: 14px;
    margin: 10px 0;
}

.qr-code-placeholder {
    min-height: 100px;
    background-color: #f9f9f9;
}

/* Footer */
.invoice-footer {
    font-size: 11px;
    color: #666;
}

.terms-section {
    background-color: #f5f5f5;
    padding: 10px;
    border-radius: 4px;
}

/* Print Styles */
@media print {
    .invoice-container {
        max-width: none;
        padding: 10px;
    }

    .invoice-header,
    .items-section,
    .totals-section {
        break-inside: avoid;
    }
}

/* Mobile Responsive */
@media (max-width: 600px) {
    .invoice-container {
        padding: 10px;
        font-size: 12px;
    }

    .company-name {
        font-size: 18px;
    }

    .invoice-title h1 {
        font-size: 20px;
    }

    .items-table {
        font-size: 10px;
    }
}
